mapper.select:
select

from sub_report sbr
left join sleep_report sr on sbr.id = sr.id
left join device d on d.`mac_address` = sbr.`mac_address`
where d.`hostipal_id` = #{hostipalId}
and sr.`user_id` = #{userId}

select

from sleep_report
where user_id = #{userId,jdbcType=CHAR}
order by
start_time desc;
